import React, { useState } from 'react';
import './App.css';

function App() {
  // Estado para la información de la empresa
  const [showInfo, setShowInfo] = useState(false);

  // Estado para el CRUD de tipos de comida
  const [menuItems, setMenuItems] = useState([
    { id: 1, name: 'Pizza Margherita', description: 'Deliciosa pizza con tomate fresco y albahaca.', price: 12.99 },
    { id: 2, name: 'Tacos de Carnitas', description: 'Tortillas rellenas de carne jugosa y salsas.', price: 9.99 },
    { id: 3, name: 'Sushi Variado', description: 'Selección de sushi fresco y sabroso.', price: 15.99 },
  ]);

  // Estado para el carrito de compras
  const [cart, setCart] = useState([]);
  const [newItem, setNewItem] = useState({ name: '', description: '', price: '' });

  // Función para agregar nuevos elementos al menú
  const handleAddItem = () => {
    if (newItem.name.trim() && newItem.description.trim() && newItem.price.trim()) {
      setMenuItems([
        ...menuItems,
        { id: Date.now(), name: newItem.name, description: newItem.description, price: parseFloat(newItem.price) },
      ]);
      setNewItem({ name: '', description: '', price: '' });
    }
  };

  // Función para eliminar elementos del menú
  const handleDeleteItem = (id) => {
    setMenuItems(menuItems.filter((item) => item.id !== id));
  };

  // Función para agregar elementos al carrito
  const handleAddToCart = (item) => {
    setCart([...cart, item]);
  };

  // Función para realizar el pago
  const handlePayment = () => {
    alert('¡Pago realizado con éxito! Gracias por tu compra.');
    setCart([]); // Limpiar carrito después del pago
  };

  return (
    <div className="App">
      {/* Encabezado */}
      <header className="App-header">
        <div className="hero">
          <h1>Tienda Gourmet Online</h1>
          <p>Explora lo mejor de la gastronomía desde la comodidad de tu hogar.</p>
        </div>
        <nav className="navigation">
          <ul>
            <li><a href="#about">Sobre Nosotros</a></li>
            <li><a href="#menu">Menú</a></li>
            <li><a href="#cart">Carrito</a></li>
          </ul>
        </nav>
      </header>

      {/* Sección Sobre Nosotros */}
      <section id="about" className="about-section">
        <h2>Sobre Nosotros</h2>
        <button onClick={() => setShowInfo(!showInfo)}>
          {showInfo ? 'Ocultar Información' : 'Mostrar Información'}
        </button>
        {showInfo && (
          <p>
            En nuestra tienda online, nos dedicamos a ofrecer una experiencia gastronómica de lujo. 
            Cada plato es preparado con ingredientes frescos y de alta calidad para garantizar una 
            experiencia única.
          </p>
        )}
      </section>

      {/* Sección de Menú */}
      <section id="menu" className="menu-section">
        <h2>Nuestro Menú</h2>

        {/* Lista dinámica del menú */}
        <div className="menu-grid">
          {menuItems.map((item) => (
            <div key={item.id} className="menu-item">
              <h3>{item.name}</h3>
              <p>{item.description}</p>
              <p><strong>Precio:</strong> ${item.price.toFixed(2)}</p>
              <button onClick={() => handleAddToCart(item)} className="add-cart-button">Agregar al Carrito</button>
              <button onClick={() => handleDeleteItem(item.id)} className="delete-button">Eliminar</button>
            </div>
          ))}
        </div>

        {/* Formulario para agregar nuevo tipo de comida */}
        <div className="menu-form">
          <h3>Agregar Nuevo Platillo</h3>
          <input
            type="text"
            name="name"
            placeholder="Nombre del platillo"
            value={newItem.name}
            onChange={(e) => setNewItem({ ...newItem, name: e.target.value })}
          />
          <input
            type="text"
            name="description"
            placeholder="Descripción"
            value={newItem.description}
            onChange={(e) => setNewItem({ ...newItem, description: e.target.value })}
          />
          <input
            type="number"
            name="price"
            placeholder="Precio"
            value={newItem.price}
            onChange={(e) => setNewItem({ ...newItem, price: e.target.value })}
          />
          <button onClick={handleAddItem} className="add-button">Agregar</button>
        </div>
      </section>

      {/* Carrito de Compras */}
      <section id="cart" className="cart-section">
        <h2>Carrito de Compras</h2>
        {cart.length === 0 ? (
          <p>El carrito está vacío.</p>
        ) : (
          <div className="cart-items">
            {cart.map((item, index) => (
              <div key={index} className="cart-item">
                <h3>{item.name}</h3>
                <p>{item.description}</p>
                <p><strong>Precio:</strong> ${item.price.toFixed(2)}</p>
              </div>
            ))}
            <div className="total">
              <h3>Total: ${cart.reduce((total, item) => total + item.price, 0).toFixed(2)}</h3>
            </div>
            <button onClick={handlePayment} className="payment-button">Proceder al Pago</button>
          </div>
        )}
      </section>

      {/* Pie de Página */}
      <footer className="App-footer">
        <p>&copy; 2024 Tienda Gourmet Online. Todos los derechos reservados.</p>
      </footer>
    </div>
  );
}

export default App;
