import React, { useState } from 'react';

const ComidaSelector = () => {
  const [tipoComida, setTipoComida] = useState('');
  const [opcionesComida, setOpcionesComida] = useState([]);
  const [comidasSeleccionadas, setComidasSeleccionadas] = useState([]);

  const tiposDeComida = {
    Italiana: ['Pizza', 'Pasta', 'Lasaña'],
    Mexicana: ['Tacos', 'Burritos', 'Nachos'],
    Japonesa: ['Sushi', 'Ramen', 'Tempura'],
    Postres: ['Pasteles', 'Helados', 'Galletas'],
  };

  const handleTipoComidaChange = (e) => {
    setTipoComida(e.target.value);
    setOpcionesComida(tiposDeComida[e.target.value] || []);
  };

  const agregarComida = (comida) => {
    setComidasSeleccionadas([...comidasSeleccionadas, comida]);
  };

  const eliminarComida = (comida) => {
    setComidasSeleccionadas(comidasSeleccionadas.filter(item => item !== comida));
  };

  return (
    <div>
      <h2>Selecciona un Tipo de Comida</h2>
      <select onChange={handleTipoComidaChange} value={tipoComida}>
        <option value="">-- Selecciona --</option>
        {Object.keys(tiposDeComida).map((tipo) => (
          <option key={tipo} value={tipo}>{tipo}</option>
        ))}
      </select>

      {opcionesComida.length > 0 && (
        <div>
          <h3>Opciones de {tipoComida}</h3>
          {opcionesComida.map((comida) => (
            <div key={comida}>
              <span>{comida}</span>
              <button onClick={() => agregarComida(comida)}>Agregar</button>
            </div>
          ))}
        </div>
      )}

      <h3>Comidas Seleccionadas</h3>
      {comidasSeleccionadas.length > 0 ? (
        <ul>
          {comidasSeleccionadas.map((comida) => (
            <li key={comida}>
              {comida}
              <button onClick={() => eliminarComida(comida)}>Eliminar</button>
            </li>
          ))}
        </ul>
      ) : (
        <p>No hay comidas seleccionadas.</p>
      )}
    </div>
  );
};

export default ComidaSelector;
