import React, { useState } from 'react';
import './App.css';

function App() {
  const [user, setUser] = useState(null);
  const [menuItems, setMenuItems] = useState([
    { id: 1, name: 'Hamburguesa Clásica', description: 'Hamburguesa con queso, lechuga y tomate.', price: 5.99 },
    { id: 2, name: 'Papas Fritas', description: 'Porción de papas fritas crujientes.', price: 2.99 },
    { id: 3, name: 'Pizza Napolitana', description: 'Pizza con tomate, albahaca y mozzarella.', price: 8.99 },
  ]);
  const [cart, setCart] = useState([]);
  const [newItem, setNewItem] = useState({ name: '', description: '', price: '' });
  const [editItem, setEditItem] = useState(null);
  const [loginInfo, setLoginInfo] = useState({ username: '', password: '' });

  const users = [
    { username: 'admin', password: 'admin123', role: 'admin' },
    { username: 'user', password: 'user123', role: 'user' },
  ];

  const handleLogin = () => {
    const foundUser = users.find(
      (u) => u.username === loginInfo.username && u.password === loginInfo.password
    );
    if (foundUser) {
      setUser(foundUser);
    } else {
      alert('Credenciales incorrectas');
    }
  };

  const handleLogout = () => {
    setUser(null);
    setCart([]);
  };

  const handleAddItem = () => {
    if (newItem.name && newItem.description && newItem.price) {
      setMenuItems([
        ...menuItems,
        { id: Date.now(), name: newItem.name, description: newItem.description, price: parseFloat(newItem.price) },
      ]);
      setNewItem({ name: '', description: '', price: '' });
    }
  };

  const handleDeleteItem = (id) => {
    setMenuItems(menuItems.filter((item) => item.id !== id));
  };

  const handleEditItem = () => {
    if (editItem && editItem.name && editItem.description && editItem.price) {
      setMenuItems(
        menuItems.map((item) => (item.id === editItem.id ? { ...editItem, price: parseFloat(editItem.price) } : item))
      );
      setEditItem(null);
    }
  };

  const handleAddToCart = (item) => {
    setCart([...cart, item]);
  };

  const handleRemoveFromCart = (id) => {
    setCart(cart.filter((item) => item.id !== id));
  };

  const handlePayment = () => {
    alert('¡Pago realizado con éxito!');
    setCart([]);
  };

  if (!user) {
    return (
      <div className="login">
        <h2>Iniciar Sesión</h2>
        <input
          type="text"
          placeholder="Usuario"
          value={loginInfo.username}
          onChange={(e) => setLoginInfo({ ...loginInfo, username: e.target.value })}
        />
        <input
          type="password"
          placeholder="Contraseña"
          value={loginInfo.password}
          onChange={(e) => setLoginInfo({ ...loginInfo, password: e.target.value })}
        />
        <button onClick={handleLogin}>Ingresar</button>
      </div>
    );
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>PedidosYa - {user.role === 'admin' ? 'Panel de Administración' : 'Bienvenido'}</h1>
        <button onClick={handleLogout} className="logout-button">Cerrar Sesión</button>
      </header>

      {user.role === 'admin' && (
        <section className="admin-section">
          <h2>Gestión del Menú</h2>
          {editItem ? (
            <div className="form">
              <h3>Editar Platillo</h3>
              <input
                type="text"
                placeholder="Nombre"
                value={editItem.name}
                onChange={(e) => setEditItem({ ...editItem, name: e.target.value })}
              />
              <input
                type="text"
                placeholder="Descripción"
                value={editItem.description}
                onChange={(e) => setEditItem({ ...editItem, description: e.target.value })}
              />
              <input
                type="number"
                placeholder="Precio"
                value={editItem.price}
                onChange={(e) => setEditItem({ ...editItem, price: e.target.value })}
              />
              <button onClick={handleEditItem}>Guardar</button>
              <button onClick={() => setEditItem(null)}>Cancelar</button>
            </div>
          ) : (
            <div className="form">
              <h3>Agregar Nuevo Platillo</h3>
              <input
                type="text"
                placeholder="Nombre"
                value={newItem.name}
                onChange={(e) => setNewItem({ ...newItem, name: e.target.value })}
              />
              <input
                type="text"
                placeholder="Descripción"
                value={newItem.description}
                onChange={(e) => setNewItem({ ...newItem, description: e.target.value })}
              />
              <input
                type="number"
                placeholder="Precio"
                value={newItem.price}
                onChange={(e) => setNewItem({ ...newItem, price: e.target.value })}
              />
              <button onClick={handleAddItem}>Agregar</button>
            </div>
          )}
        </section>
      )}

      <section className="menu-section">
        <h2>Menú</h2>
        <div className="menu-grid">
          {menuItems.map((item) => (
            <div key={item.id} className="menu-item">
              <h3>{item.name}</h3>
              <p>{item.description}</p>
              <p>Precio: ${item.price.toFixed(2)}</p>
              {user.role === 'admin' ? (
                <>
                  <button onClick={() => setEditItem(item)}>Editar</button>
                  <button onClick={() => handleDeleteItem(item.id)}>Eliminar</button>
                </>
              ) : (
                <button onClick={() => handleAddToCart(item)}>Agregar al Carrito</button>
              )}
            </div>
          ))}
        </div>
      </section>

      {user.role === 'user' && (
        <section className="cart-section">
          <h2>Carrito de Compras</h2>
          {cart.length === 0 ? (
            <p>El carrito está vacío.</p>
          ) : (
            <div className="cart-items">
              {cart.map((item, index) => (
                <div key={index} className="cart-item">
                  <h3>{item.name}</h3>
                  <p>{item.description}</p>
                  <p>Precio: ${item.price.toFixed(2)}</p>
                  <button onClick={() => handleRemoveFromCart(item.id)}>Eliminar</button>
                </div>
              ))}
              <h3>Total: ${cart.reduce((total, item) => total + item.price, 0).toFixed(2)}</h3>
              <button onClick={handlePayment}>Pagar</button>
            </div>
          )}
        </section>
      )}

      <footer className="App-footer">
        <p>© 2024 PedidosYa Clone. Todos los derechos reservados.</p>
      </footer>
    </div>
  );
}

export default App;
