import React, { useState } from 'react';
import './App.css';

function App() {
  // Estado para el CRUD de tipos de comida
  const [menuItems, setMenuItems] = useState([
    { id: 1, name: 'Comida Italiana', description: 'Pizza, pasta, lasaña y más.' },
    { id: 2, name: 'Comida Mexicana', description: 'Tacos, burritos, nachos y guacamole.' },
    { id: 3, name: 'Comida Japonesa', description: 'Sushi, ramen, tempura y más.' },
    { id: 4, name: 'Postres', description: 'Pasteles, helados y postres deliciosos.' },
  ]);

  const [newItem, setNewItem] = useState({ name: '', description: '' });

  // Función para manejar la adición de nuevos elementos
  const handleAddItem = () => {
    if (newItem.name.trim() && newItem.description.trim()) {
      setMenuItems([
        ...menuItems,
        { id: Date.now(), name: newItem.name, description: newItem.description },
      ]);
      setNewItem({ name: '', description: '' }); // Limpiar formulario
    }
  };

  // Función para eliminar un elemento
  const handleDeleteItem = (id) => {
    setMenuItems(menuItems.filter((item) => item.id !== id));
  };

  // Función para manejar cambios en el formulario
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewItem({ ...newItem, [name]: value });
  };

  return (
    <div className="App">
      {/* Encabezado */}
      <header className="App-header">
        <h1>Tienda E-Commerce de Comida</h1>
        <nav>
          <ul>
            <li><a href="#about">Sobre Nosotros</a></li>
            <li><a href="#menu">Menú</a></li>
          </ul>
        </nav>
      </header>

      {/* Sección de Información */}
      <section id="about" className="about-section">
        <h2>Sobre Nosotros</h2>
        <p>
          Bienvenido a nuestra tienda de comida en línea. Ofrecemos una gran variedad de comidas
          deliciosas, preparadas con ingredientes frescos y de alta calidad. Nuestro objetivo es 
          brindar a nuestros clientes una experiencia culinaria única desde la comodidad de su hogar.
        </p>
      </section>

      {/* Sección de Menú */}
      <section id="menu" className="menu-section">
        <h2>Menú de Tipos de Comida</h2>

        {/* Lista dinámica del menú */}
        <div className="menu-grid">
          {menuItems.map((item) => (
            <div key={item.id} className="menu-item">
              <h3>{item.name}</h3>
              <p>{item.description}</p>
              <button onClick={() => handleDeleteItem(item.id)} className="delete-button">Eliminar</button>
            </div>
          ))}
        </div>

        {/* Formulario para agregar nuevo tipo de comida */}
        <div className="menu-form">
          <h3>Agregar Nuevo Tipo de Comida</h3>
          <input
            type="text"
            name="name"
            placeholder="Nombre de la comida"
            value={newItem.name}
            onChange={handleInputChange}
          />
          <input
            type="text"
            name="description"
            placeholder="Descripción"
            value={newItem.description}
            onChange={handleInputChange}
          />
          <button onClick={handleAddItem} className="add-button">Agregar</button>
        </div>
      </section>

      {/* Pie de página */}
      <footer className="App-footer">
        <p>&copy; 2024 Tienda E-Commerce de Comida. Todos los derechos reservados.</p>
      </footer>
    </div>
  );
}

export default App;
