import React, { useState } from 'react';
import './App.css';

function App() {
  const [user, setUser] = useState(null); // Estado para controlar el usuario actual (admin o cliente)
  const [menuItems, setMenuItems] = useState([
    { id: 1, name: 'Pizza Margherita', description: 'Deliciosa pizza con tomate fresco y albahaca.', price: 12.99 },
    { id: 2, name: 'Tacos de Carnitas', description: 'Tortillas rellenas de carne jugosa y salsas.', price: 9.99 },
    { id: 3, name: 'Sushi Variado', description: 'Selección de sushi fresco y sabroso.', price: 15.99 },
  ]);
  const [cart, setCart] = useState([]);
  const [newItem, setNewItem] = useState({ name: '', description: '', price: '' });
  const [editItem, setEditItem] = useState(null);

  // Estado para login
  const [loginInfo, setLoginInfo] = useState({ username: '', password: '' });

  // Simulación de usuarios
  const users = [
    { username: 'admin', password: 'admin123', role: 'admin' },
    { username: 'user', password: 'user123', role: 'user' },
  ];

  const handleLogin = () => {
    const foundUser = users.find(
      (u) => u.username === loginInfo.username && u.password === loginInfo.password
    );
    if (foundUser) {
      setUser(foundUser);
    } else {
      alert('Credenciales incorrectas');
    }
  };

  const handleLogout = () => {
    setUser(null);
    setCart([]);
  };

  const handleAddItem = () => {
    if (newItem.name.trim() && newItem.description.trim() && newItem.price.trim()) {
      setMenuItems([
        ...menuItems,
        { id: Date.now(), name: newItem.name, description: newItem.description, price: parseFloat(newItem.price) },
      ]);
      setNewItem({ name: '', description: '', price: '' });
    }
  };

  const handleDeleteItem = (id) => {
    setMenuItems(menuItems.filter((item) => item.id !== id));
  };

  const handleEditItem = () => {
    if (editItem && editItem.name.trim() && editItem.description.trim() && editItem.price.trim()) {
      setMenuItems(
        menuItems.map((item) => (item.id === editItem.id ? { ...editItem, price: parseFloat(editItem.price) } : item))
      );
      setEditItem(null);
    }
  };

  const handleAddToCart = (item) => {
    setCart([...cart, item]);
  };

  const handleRemoveFromCart = (id) => {
    setCart(cart.filter((item) => item.id !== id));
  };

  const handlePayment = () => {
    alert('¡Pago realizado con éxito! Gracias por tu compra.');
    setCart([]);
  };

  if (!user) {
    return (
      <div className="login">
        <h2>Iniciar Sesión</h2>
        <input
          type="text"
          placeholder="Usuario"
          value={loginInfo.username}
          onChange={(e) => setLoginInfo({ ...loginInfo, username: e.target.value })}
        />
        <input
          type="password"
          placeholder="Contraseña"
          value={loginInfo.password}
          onChange={(e) => setLoginInfo({ ...loginInfo, password: e.target.value })}
        />
        <button onClick={handleLogin}>Ingresar</button>
      </div>
    );
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>Bienvenido, {user.role === 'admin' ? 'Administrador' : 'Cliente'}</h1>
        <button onClick={handleLogout} className="logout-button">Cerrar Sesión</button>
      </header>

      {user.role === 'admin' && (
        <section className="admin-section">
          <h2>Administrar Menú</h2>

          {editItem ? (
            <div className="menu-form">
              <h3>Editar Platillo</h3>
              <input
                type="text"
                placeholder="Nombre del platillo"
                value={editItem.name}
                onChange={(e) => setEditItem({ ...editItem, name: e.target.value })}
              />
              <input
                type="text"
                placeholder="Descripción"
                value={editItem.description}
                onChange={(e) => setEditItem({ ...editItem, description: e.target.value })}
              />
              <input
                type="number"
                placeholder="Precio"
                value={editItem.price}
                onChange={(e) => setEditItem({ ...editItem, price: e.target.value })}
              />
              <button onClick={handleEditItem} className="edit-button">Guardar Cambios</button>
              <button onClick={() => setEditItem(null)} className="cancel-button">Cancelar</button>
            </div>
          ) : (
            <div className="menu-form">
              <h3>Agregar Nuevo Platillo</h3>
              <input
                type="text"
                placeholder="Nombre del platillo"
                value={newItem.name}
                onChange={(e) => setNewItem({ ...newItem, name: e.target.value })}
              />
              <input
                type="text"
                placeholder="Descripción"
                value={newItem.description}
                onChange={(e) => setNewItem({ ...newItem, description: e.target.value })}
              />
              <input
                type="number"
                placeholder="Precio"
                value={newItem.price}
                onChange={(e) => setNewItem({ ...newItem, price: e.target.value })}
              />
              <button onClick={handleAddItem} className="add-button">Agregar</button>
            </div>
          )}
        </section>
      )}

      <section className="menu-section">
        <h2>{user.role === 'admin' ? 'Menú Disponible' : 'Nuestro Menú'}</h2>
        <div className="menu-grid">
          {menuItems.map((item) => (
            <div key={item.id} className="menu-item">
              <h3>{item.name}</h3>
              <p>{item.description}</p>
              <p><strong>Precio:</strong> ${item.price.toFixed(2)}</p>
              {user.role === 'admin' ? (
                <>
                  <button onClick={() => setEditItem(item)} className="edit-button">Editar</button>
                  <button onClick={() => handleDeleteItem(item.id)} className="delete-button">Eliminar</button>
                </>
              ) : (
                <button onClick={() => handleAddToCart(item)} className="add-cart-button">Agregar al Carrito</button>
              )}
            </div>
          ))}
        </div>
      </section>

      {user.role === 'user' && (
        <section className="cart-section">
          <h2>Carrito de Compras</h2>
          {cart.length === 0 ? (
            <p>El carrito está vacío.</p>
          ) : (
            <div className="cart-items">
              {cart.map((item, index) => (
                <div key={index} className="cart-item">
                  <h3>{item.name}</h3>
                  <p>{item.description}</p>
                  <p><strong>Precio:</strong> ${item.price.toFixed(2)}</p>
                  <button onClick={() => handleRemoveFromCart(item.id)} className="delete-button">Eliminar</button>
                </div>
              ))}
              <div className="total">
                <h3>Total: ${cart.reduce((total, item) => total + item.price, 0).toFixed(2)}</h3>
              </div>
              <button onClick={handlePayment} className="payment-button">Proceder al Pago</button>
            </div>
          )}
        </section>
      )}

      <footer className="App-footer">
        <p>&copy; 2024 Tienda Gourmet Online. Todos los derechos reservados.</p>
      </footer>
    </div>
  );
}

export default App;
